// CertificateAuthorityKey returns the private key for the CA
func CertificateAuthorityKey() *rsa.PrivateKey {
	blk, _ := pem.Decode([]byte(TestCertificateAuthorityKey))
	pkey, err := x509.ParsePKCS1PrivateKey(blk.Bytes)
	if err != nil {
		panic(err)
	}
	return pkey
}

// CertificateAuthorityCert returns the certificate for the CA
func CertificateAuthorityCert() *x509.Certificate {
	blk, _ := pem.Decode([]byte(TestCertificateAuthorityPem))
	cert, err := x509.ParseCertificate(blk.Bytes)
	if err != nil {
		panic(err)
	}
	return cert
}
